mapper.select:
select open_class.*, c.class_id as classId, c.class_name as className, t.teacher_id, t.teacher_name as teacherName, c.class_score as score, t.position as position, d.depart_name as departName from open_class join class c on open_class.class_record = c.id join teacher t on open_class.teacher_id = t.teacher_id join department d on c.depart_id = d.depart_id
and c.class_id = #{classId}
and c.class_name like concat('%', #{className}, '%')
and t.teacher_name like concat('%', #{teacherName}, '%')
and d.depart_name like concat('%', #{departName}, '%')
and c.class_score = #{classScore}
